import React, { useState } from "react";
import '../css/CreateSunfish.css'

function CreateSunfish({ onSuccess }) {
    const [name, setName] = useState("");
    const [error, setError] = useState("");

    const handleCreate = async () => {
        try {
            const response = await fetch("http://127.0.0.1:8000/api/sunfish/", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ name }),
            });
            if (!response.ok) {
                const data = await response.json();
                throw new Error(data.error || "Failed to create sunfish");
            }
            setName("");
            setError("");
            onSuccess(); // Refresh the parent component
        } catch (err) {
            setError(err.message);
        }
    };

    return (
        <div>
            <h3>Create New Sunfish</h3>
            <input
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                placeholder="Enter sunfish name"
            />
            <button onClick={handleCreate}>Create</button>
            {error && <p style={{ color: "red" }}>{error}</p>}
        </div>
    );
}

export default CreateSunfish;
