import React, { useEffect, useState } from 'react';
import '../css/ContributionCalendar.css';

function ContributionCalendar() {
    const [contributions, setContributions] = useState([]); // 초기값을 빈 배열로 설정
    const [loading, setLoading] = useState(true); // 로딩 상태 추가
    const [error, setError] = useState(null); // 에러 상태

    useEffect(() => {
        async function fetchBackendContributions() {
            try {
                const response = await fetch('http://127.0.0.1:8000/api/contributions/'); // 백엔드 API URL
                if (!response.ok) {
                    throw new Error(`Failed to fetch: ${response.status}`);
                }

                const data = await response.json();
                console.log('Backend Contribution API Response:', data);

                setContributions(data); // 데이터 설정
                setLoading(false); // 로딩 완료
            } catch (err) {
                console.error('Error fetching contributions:', err.message);
                setError(err.message);
                setLoading(false); // 로딩 완료
            }
        }

        fetchBackendContributions();
    }, []);

    if (loading) {
        return <div>Loading contributions...</div>;
    }

    if (error) {
        return (
            <div className="error-message">
                <p>Failed to load contributions: {error}</p>
            </div>
        );
    }

    return (
        <div className="contribution-calendar">
            {weeks.map((week, weekIndex) => (
                <div key={weekIndex} className="week-column">
                    {week.map((day, dayIndex) => (
                        <div
                            key={dayIndex}
                            className={`day-cell level-${Math.min(day.count, 4)}`}
                            title={`Date: ${day.date}, Contributions: ${day.count}`}
                        ></div>
                    ))}
                </div>
            ))}
        </div>
    );
}

export default ContributionCalendar;
