import React, { useState, useEffect } from "react";
import "../css/ContributionDisplay.css";

function ContributionDisplay() {
    const [contributions, setContributions] = useState([]);

    useEffect(() => {
        const fetchContributions = async () => {
            try {
                const response = await fetch("http://127.0.0.1:8000/api/contributions/");
                const data = await response.json();
                setContributions(data);
            } catch (error) {
                console.error("Error fetching contributions:", error);
            }
        };

        fetchContributions();
    }, []);

    return (
        <div className="contribution-display">
            <h3>Contributions</h3>
            <ul>
                {contributions.map((contribution, index) => (
                    <li key={index}>{`${contribution.date}: ${contribution.count}`}</li>
                ))}
            </ul>
        </div>
    );
}

export default ContributionDisplay;
