import React, { useState, useEffect } from 'react';

function OceanSunFish() {
    const [level, setLevel] = useState(1);
    const [stage, setStage] = useState('dust');
    const [contributions, setContributions] = useState([]);

    useEffect(() => {
        async function fetchContributions() {
            try {
                const response = await fetch('/api/github/xeohyun/'); // API 호출
                const data = await response.json();
                setContributions(data);
            } catch (error) {
                console.error("Error fetching contributions:", error);
            }
        }

        fetchContributions();
    }, []);

    useEffect(() => {
        if (contributions.length > 0) {
            const todayContribution = contributions.find(
                (day) => new Date(day.date).toISOString().slice(0, 10) === new Date().toISOString().slice(0, 10)
            );
            const contributionCount = todayContribution ? todayContribution.count : 0;

            setLevel((prevLevel) => Math.min(prevLevel + contributionCount, 50));

            if (level >= 1 && level <= 3) setStage('dust');
            else if (level >= 4 && level <= 10) setStage('baby');
            else if (level >= 11 && level <= 30) setStage('adult');
            else if (level >= 31 && level <= 50) setStage('king');
            else setStage('new');
        }
    }, [contributions, level]);

    return (
        <div>
            <h2>Ocean Sunfish Game</h2>
            <p>Level: {level}</p>
            <p>Stage: {stage}</p>
            <div className="sunfish-stage">
                <img src={`/img/${stage}.png`} alt={`${stage}`} />
            </div>
        </div>
    );
}

export default OceanSunFish;
