import React, { useState, useEffect } from "react";
import dustLeft from "../img/dust_left.png";
import dustRight from "../img/dust_right.png";
import fishLeft from "../img/adult_left.png";
import kingLeft from "../img/king_left.png";
import "../css/OceanSunFish.css";

function OceanSunFishStatus() {
    const [sunfishList, setSunfishList] = useState([]); // Sunfish 목록
    const [dropdownOpen, setDropdownOpen] = useState(false); // 드롭다운 상태

    const images = {
        dust: dustLeft,
        baby: dustRight,
        adult: fishLeft,
        king: kingLeft,
    };

    // API에서 데이터를 가져오는 함수
    useEffect(() => {
        const fetchSunfishData = async () => {
            try {
                const response = await fetch("http://127.0.0.1:8000/api/sunfish-with-contributions/");
                if (!response.ok) {
                    throw new Error("Failed to fetch sunfish data");
                }
                const data = await response.json();
                setSunfishList(data);
            } catch (error) {
                console.error("Error fetching sunfish data:", error);
            }
        };

        fetchSunfishData();
    }, []);

    if (sunfishList.length === 0) {
        return <div>Loading Sunfish...</div>; // 데이터가 없을 경우 로딩 메시지
    }

    // 단계(stage) 계산
    const getStage = (level) => {
        if (level >= 1 && level <= 3) {
            return "dust";
        } else if (level >= 4 && level <= 10) {
            return "baby";
        } else if (level >= 11 && level <= 30) {
            return "adult";
        } else if (level >= 31 && level <= 50) {
            return "king";
        }
        return "dust"; // 기본값
    };

    return (
        <div className="ocean-level-container">
            {sunfishList.map((sunfish) => {
                const stage = getStage(sunfish.level);
                const currentImage = images[stage];

                return (
                    <div
                        key={sunfish.id}
                        className="current-sunfish-container"
                        onClick={() => setDropdownOpen((prev) => !prev)} // 클릭 시 드롭다운 토글
                    >
                        <h2>{sunfish.name}</h2>
                        <h3>Level: {sunfish.level}</h3>
                        <p>Stage: {stage}</p>
                        <p>Status: {sunfish.is_alive ? "Alive" : "Dead"}</p>
                        <div className="ocean-image">
                            <img src={currentImage} alt={stage} />
                        </div>
                        <p>Today's Contribution: {sunfish.daily_contribution || 0}</p>
                        <p>Total Contribution: {sunfish.total_contribution || 0}</p>
                    </div>
                );
            })}

            {/* 드롭다운: 모든 Sunfish 표시 */}
            {dropdownOpen && (
                <div className="sunfish-dropdown">
                    {sunfishList.map((sunfish) => {
                        const stage = getStage(sunfish.level);
                        const image = images[stage];

                        return (
                            <div key={sunfish.id} className="dropdown-sunfish-item">
                                <img src={image} alt={stage} className="dropdown-sunfish-image" />
                                <div className="dropdown-sunfish-info">
                                    <p>Name: {sunfish.name}</p>
                                    <p>Level: {sunfish.level}</p>
                                    <p>Status: {sunfish.is_alive ? "Alive" : "Dead"}</p>
                                    <p>Total Contribution: {sunfish.total_contribution || 0}</p>
                                </div>
                            </div>
                        );
                    })}
                </div>
            )}
        </div>
    );
}

export default OceanSunFishStatus;
