            <p>Status: {isAlive ? 'Alive' : 'Dead'}</p>
            <div className="ocean-image">
                <img
                    src={`../img/ocean-${stage}.png`}
                    alt={stage}
                />
            </div>
            <p>Today's Contribution: {dailyContribution}</p>
        </div>
    );
}

