import React, { useEffect, useState } from 'react';
import '../css/ContributionCalendar.css';

function ContributionCalendar() {
    const [contributions, setContributions] = useState([]); // 기여 데이터
    const [error, setError] = useState(null); // 에러 상태

    useEffect(() => {
        async function fetchBackendContributions() {
            try {
                const response = await fetch('http://127.0.0.1:8000/api/contributions/'); // 백엔드 API URL
                if (!response.ok) {
                    throw new Error(`Failed to fetch: ${response.status}`);
                }

                const data = await response.json(); // 백엔드에서 데이터를 가져옴
                console.log('Backend Contribution API Response:', data);

                // 데이터를 평탄화하여 contributions 배열 생성
                const flatContributions = data.flatMap((week) =>
                    week.contributionDays.map((day) => ({
                        date: day.date,
                        commits: day.contributionCount,
                        level: (() => {
                            const count = day.contributionCount;
                            if (count === 0) return 0;
                            if (count <= 3) return 1;
                            if (count <= 9) return 2;
                            if (count <= 19) return 3;
                            return 4;
                        })(),
                    }))
                );

                console.log('Flattened Contributions:', flatContributions);
                setContributions(flatContributions); // 정리된 데이터를 상태로 설정
            } catch (err) {
                console.error('Error fetching contributions:', err.message);
                setError(err.message);
            }
        }

        fetchBackendContributions();
    }, []);

    // 데이터를 주 단위로 분리
    const weeks = Array.from({ length: Math.ceil(contributions.length / 7) }, (_, weekIndex) =>
        contributions.slice(weekIndex * 7, weekIndex * 7 + 7)
    );

    return (
        <div className="contribution-calendar">
            {error ? (
                <div className="error-message">
                    <p>Failed to load contributions: {error}</p>
                </div>
            ) : (
                <div className="calendar-grid">
                    {weeks.map((week, weekIndex) => (
                        <div key={weekIndex} className="week-column">
                            {week.map((day, dayIndex) => (
                                <div
                                    key={dayIndex}
                                    className={`day-cell level-${day.level}`} // `level-X` 클래스를 추가
                                    title={`${day.date}: ${day.commits || 0} contributions`}
                                ></div>
                            ))}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default ContributionCalendar;
