import React, { useState, useEffect } from 'react';
import { calculateLevelUp } from '../utils/levelUpManager'; // 레벨업 로직 import
import dustLeft from '../img/dust_left.png';
import dustRight from '../img/dust_right.png';
import fishLeft from '../img/adult_left.png';
import kingLeft from '../img/king_left.png';
import '../css/OceanSunFish.css';

function OceanSunFishStatus() {
    const [sunfish, setSunfish] = useState(null);
    const [allSunfish, setAllSunfish] = useState([]);
    const [dropdownOpen, setDropdownOpen] = useState(false);

    const images = {
        dust: dustLeft,
        baby: dustRight,
        adult: fishLeft,
        king: kingLeft,
    };

    // Sunfish 데이터를 가져오는 함수
    const fetchSunfishData = async () => {
        try {
            const response = await fetch('http://127.0.0.1:8000/api/sunfish/');
            if (!response.ok) {
                throw new Error('Failed to fetch sunfish data');
            }
            const data = await response.json();

            if (Array.isArray(data) && data.length > 0) {
                const latestSunfish = data[data.length - 1];
                setSunfish(latestSunfish);
                setAllSunfish(data);
                console.log('Sunfish data fetched successfully:', data);

                // 레벨업 처리
                await handleLevelUp(latestSunfish.id, latestSunfish.level);
            } else {
                setSunfish(null);
                setAllSunfish([]);
            }
        } catch (error) {
            console.error('Error fetching sunfish data:', error);
        }
    };

    // 컴포넌트 로드 시 Sunfish 데이터 가져오기
    useEffect(() => {
        fetchSunfishData();
    }, []);

    // 기여도를 업데이트하고 레벨업 처리
    const updateContribution = async (sunfishId, newContribution) => {
        try {
            const response = await fetch('http://127.0.0.1:8000/api/contributions/', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    sunfish_id: sunfishId,
                    count: newContribution, // 추가된 기여도
                }),
            });

            if (!response.ok) {
                throw new Error('Failed to update contributions');
            }

            const data = await response.json();
            console.log('Updated Contribution Response:', data);

            // 기여도 업데이트 후 Sunfish 데이터 다시 가져오기
            fetchSunfishData(); // 여기서 호출
        } catch (error) {
            console.error('Error updating contributions:', error);
        }
    };

    // 레벨업 처리 함수
    const handleLevelUp = async (sunfishId, currentLevel) => {
        try {
            const response = await fetch(`http://127.0.0.1:8000/api/contributions/${sunfishId}/`);
            if (!response.ok) {
                throw new Error('Failed to fetch contributions');
            }
            const contributions = await response.json();

            // 오늘의 기여도 합산
            const totalContributions = contributions.reduce((sum, entry) => sum + entry.count, 0);

            // `levelUpManager`를 사용해 새로운 레벨 계산
            const { level, stage } = calculateLevelUp(currentLevel, totalContributions);

            // Sunfish 업데이트
            const updatedSunfish = { ...sunfish, level, stage };
            setSunfish(updatedSunfish);

            // 새로운 기여도를 서버에 업데이트
            await updateContribution(sunfishId, totalContributions);
        } catch (error) {
            console.error('Error updating level:', error);
        }
    };

    if (!sunfish) {
        return <div>Loading Sunfish...</div>;
    }

    const stage = sunfish.stage;
    const currentImage = images[stage];

    return (
        <div className="ocean-level-container">
            <div
                className="current-sunfish-container"
                onClick={() => setDropdownOpen((prev) => !prev)}
            >
                <h2>Level: {sunfish.level}</h2>
                <p>Stage: {stage}</p>
                <p>Status: {sunfish.is_alive ? 'Alive' : 'Dead'}</p>
                <div className="ocean-image">
                    <img src={currentImage} alt={stage} />
                </div>
                <p className="dropdown-toggle">{dropdownOpen ? 'Hide All Fish ▲' : 'Show All Fish ▼'}</p>
            </div>

            {dropdownOpen && (
                <div className="sunfish-dropdown">
                    {allSunfish.map((fish) => {
                        const fishStage = fish.stage;
                        const fishImage = images[fishStage];

                        return (
                            <div key={fish.id} className="dropdown-sunfish-item">
                                <img src={fishImage} alt={fishStage} className="dropdown-sunfish-image" />
                                <div className="dropdown-sunfish-info">
                                    <p>Name: {fish.name}</p>
                                    <p>Status: {fish.is_alive ? 'Alive' : 'Dead'}</p>
                                </div>
                            </div>
                        );
                    })}
                </div>
            )}
        </div>
    );
}

export default OceanSunFishStatus;
