import React, { useEffect, useState } from 'react';
import '../css/ContributionCalendar.css';
import { CONTRIBUTION_QUERY } from '../queries/githubQueries'; // GraphQL Query 가져오기

function ContributionCalendar() {
    const [contributions, setContributions] = useState([]); // 기여 데이터
    const [error, setError] = useState(null); // 에러 상태
    const username = 'xeohyun'; // 사용자 이름
    const token = process.env.REACT_APP_GITHUB_TOKEN; // 환경 변수에서 토큰 가져오기

    useEffect(() => {
        async function fetchGitHubContributions() {
            try {
                console.log("Fetching contributions for:", username);
                console.log("Using token:", token ? "Token is set" : "Token is missing");

                // GraphQL API 호출
                const response = await fetch('https://api.github.com/graphql', {
                    method: 'POST',
                    headers: {
                        Authorization: `Bearer ${token}`, // Bearer 토큰 사용
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        query: CONTRIBUTION_QUERY, // GraphQL Query 전달
                        variables: { username }, // Query 변수 전달
                    }),
                });

                console.log("Response status:", response.status);

                if (!response.ok) {
                    throw new Error(`Failed to fetch: ${response.status}`);
                }

                const responseData = await response.json();
                console.log("GraphQL API Response:", responseData);

                // GraphQL 응답 데이터 파싱
                const weeks =
                    responseData.data.user.contributionsCollection.contributionCalendar.weeks;

                // weeks 데이터를 1차원 배열로 평탄화(flatten) 처리
                const contributionDays = weeks.flatMap((week) =>
                    week.contributionDays.map((day) => ({
                        date: day.date,
                        commits: day.contributionCount,
                        color: day.color,
                    }))
                );

                console.log("Parsed contributions:", contributionDays);
                setContributions(contributionDays);
            } catch (err) {
                console.error("Error fetching contributions:", err.message);
                setError(err.message);
            }
        }

        fetchGitHubContributions();
    }, []);

    return (
        <div className="contribution-calendar">
            {error ? (
                <div className="error-message">
                    <p>Failed to load contributions: {error}</p>
                </div>
            ) : (
                <div className="calendar-grid">
                    {contributions.map((day, index) => (
                        <div
                            key={index}
                            className="day-cell"
                            title={`${day.date}: ${day.commits} contributions`}
                            style={{ backgroundColor: day.color }}
                        ></div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default ContributionCalendar;
