import React, { useEffect, useState } from 'react';
import '../css/ContributionCalendar.css';

function ContributionCalendar() {
    const [contributions, setContributions] = useState([]);
    const [error, setError] = useState(null);
    const username = 'xeohyun';
    const token = process.env.REACT_APP_GITHUB_TOKEN;

    useEffect(() => {
        async function fetchGitHubContributions() {
            try {
                console.log("Fetching contributions for:", username);
                console.log("Using token:", token ? "Token is set" : "Token is missing");

                const response = await fetch(
                    `https://api.github.com/users/${username}/events/public`,
                    {
                        headers: {
                            Authorization: `token ${token}`,
                        },
                    }
                );

                console.log("Response status:", response.status);

                if (!response.ok) {
                    throw new Error(`Failed to fetch: ${response.status}`);
                }

                const data = await response.json();
                console.log("Fetched data:", data);

                const contributionMap = {};
                data.forEach((event) => {
                    if (event.created_at) { // created_at 필드 존재 여부 확인
                        const date = event.created_at.split('T')[0]; // 날짜 추출
                        contributionMap[date] = (contributionMap[date] || 0) + 1;
                    }
                });

                const sortedData = Object.entries(contributionMap).map(([date, commits]) => ({
                    date,
                    commits: Math.min(commits, 4),
                }));

                console.log("Parsed contributions:", sortedData);
                setContributions(sortedData);
            } catch (err) {
                console.error("Error fetching contributions:", err.message);
                setError(err.message);
            }
        }

        fetchGitHubContributions();
    }, []);

    return (
        <div className="contribution-calendar">
            {error ? (
                <div className="error-message">
                    <p>Failed to load contributions: {error}</p>
                </div>
            ) : (
                <div className="calendar-grid">
                    {contributions.map((day, index) => (
                        <div
                            key={index}
                            className={`day-cell level-${day.commits}`}
                            title={`${day.date}: ${day.commits} commits`}
                        ></div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default ContributionCalendar;
